// import CourseCard from '../components';
import CollegeCards from '../../components/CollegeCards';
import '../../assets/css/CoursePage.css';
import { Link } from 'react-router-dom';

// Sample data for courses
const courses = [
  {
    id: 1,
    name: 'Sri Krishna ',
    image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQSC6Am_1m91SCJEQfMhjbLVJ1d-0w8aRSnznWXrOAGfQ&s',
    city: 50000,
    rating: 20,
    description: 'Learn the fundamentals of computer science and programming.',
  },
  {
    id: 2,
    name: 'PSG College of Technology ',
    image: 'data:image/jpeg;base64,',
    city: 70000,
    rating: 15,
    
    description: 'Explore data analysis, machine learning, and big data technologies.',
  },
  {
    id: 3,
    name: 'Web Development',
    image: 'data:image/jpeg;base64,',
    city: 70000,
    rating: 15,
    
    description: 'Explore data analysis, machine learning, and big data technologies.',
  },
];

function CollegePage() {
  return (
    <div className='container'>
      <div className="course-page">
        <h1>Available Colleges</h1>
        <div className="course-list">
          {courses.map(course => (
            <CollegeCards key={course.id} course={course} />
          ))}
        </div>
        {/* <button className="apply-button">APPLY NOW</button> */}
        <Link to="/educonnect/user/apply" className="apply-button">APPLY NOW</Link>
      </div>
    </div>
  );
}

export default CollegePage;
